	{ quote: "quote3", author: "author1" }, 
	{ quote: "quote4", author: "author1" }, { quote: "quote5", author: "author1" }]
	return (
		<div className="container">
			<h1>
				<i className="fa-quote-left">"</i>
				<span className="quote" id="quote">test quote</span>
				<i className="fa-quote-right">"</i>
			</h1>
			<p className="author" id="author"></p>

			<hr />
			<div className="buttons">
				{/* <a className="twitter" id="tweet" data-size="large" target="_blank" rel="noopener noreferrer"><i className="fab fa-twitter"></i></a> */}

				{/* <!--add an onclick event on 'next quote' button--> */}
				<button className="next" onclick="getNewQuote()">Next quote</button>
			</div>
		</div>

	);
}

export default App;
