function App() {
  return (
    <div classN="container">
      <h1>
        <i classN="fas fa-quote-left"></i>
        <span classN="quote" id="quote">test quote</span>
        <i classN="fas fa-quote-right"></i>
      </h1>
      <p classN="author" id="author"></p>

      <hr />
      <div classN="buttons">
        <a classN="twitter" id="tweet" data-size="large" target="_blank" rel="noopener noreferrer"><i classN="fab fa-twitter"></i></a>

        {/* <!--add an onclick event on 'next quote' button--> */}
        <button classN="next" onclick="getNewQuote()">Next quote</button>
      </div>
    </div>

  );
}

export default App;
